import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter_custom_clippers/flutter_custom_clippers.dart';import 'home_page.dart';class SignInPage extends StatefulWidget {  @override  _SignInPageState createState() => _SignInPageState();}class _SignInPageState extends State<SignInPage> {  FirebaseAuth auth = FirebaseAuth.instance;  TextEditingController _emailController = TextEditingController();  TextEditingController _passwordController = TextEditingController();  @override  Widget build(BuildContext context) {    return Scaffold(      body: Container(        height: MediaQuery.of(context).size.height,        width: MediaQuery.of(context).size.width,        child: Column(          children: [            ClipPath(              clipper: OvalBottomBorderClipper(),              child: Container(                height: 100,                color: Colors.deepOrangeAccent,              ),            ),            SizedBox(height: 50,),            Column(              mainAxisAlignment: MainAxisAlignment.center,              children: [                Text('AYUVANI',style: TextStyle(color: Colors.deepOrange,                    fontSize: 40,fontWeight: FontWeight.bold),),                Text('Get Healthcare Anywhere',style: TextStyle(color: Colors.black87,                    fontSize: 15),),                Container(                  padding: EdgeInsets.symmetric(horizontal: 40),                  //color: Colors.blueAccent.shade100,                  //height: MediaQuery.of(context).size.height / 3,                  //width: MediaQuery.of(context).size.width / 1.5,                  child: Column(                    children: [                      Container(                        child: Column(                          crossAxisAlignment: CrossAxisAlignment.start,                          children: [                            Text('Username'),                            SizedBox(height: 5,),                            TextField(                              controller: _emailController,                              decoration: InputDecoration(                                contentPadding: EdgeInsets.symmetric(horizontal: 5),                                border: OutlineInputBorder(                                  borderRadius: BorderRadius.circular(10),                                ),                              ),                            ),                            SizedBox(height: 20,),                            Text('Password'),                            SizedBox(height: 5,),                            TextField(                              controller: _passwordController,                              decoration: InputDecoration(                                contentPadding: EdgeInsets.symmetric(horizontal: 5),                                border: OutlineInputBorder(                                  borderRadius: BorderRadius.circular(10),                                ),                              ),                            ),                          ],                        ),                        margin: EdgeInsets.symmetric(horizontal: 20),                      ),                      Row(                        mainAxisAlignment: MainAxisAlignment.start,                        children: [                          Checkbox(                              value: false, onChanged: null),                          Text('Remember Me'),                          Spacer(),                          Text('Forget?'),                        ],                      ),                    ],                  ),                ),                Container(                    child: Row(                      mainAxisAlignment: MainAxisAlignment.spaceEvenly,                      children: [                        Text('Sign in With OTP',style: TextStyle(color: Colors.pinkAccent.shade200),),                        SizedBox(width: 5,),                        RaisedButton(                            child: Text('Sign In',style: TextStyle(color: Colors.white),),                            color: Colors.pinkAccent.shade200,                            onPressed: () async{                              try{                                await auth.signInWithEmailAndPassword(email: _emailController.text,                                    password: _passwordController.text                                );                                return Navigator.push(context, MaterialPageRoute(builder: (context)=> HomePage()));                              }on FirebaseAuthException catch(e){                                print(e.message);                              }                            }                        ),                      ],                    )                ),              ],            ),          ],        )      ),    );  }}